sample.samplerData:
import com.auth0.jwt.JWT
import com.auth0.jwt.algorithms.Algorithm

try
{
	//This variable contains the jwtSecret extracted from the onboard response
	def secret = vars.get("jwtSecret")

	//create the JWT Token with the secret
	Algorithm algorithm = Algorithm.HMAC256(secret)
	def token = JWT.create().withIssuer("auth0").sign(algorithm)

	//generated JWT Token is assigned to variable to used throughout the execution.
	vars.put("jwtToken", token)

	//The payer ID extracted from the onboard response is assigned to variable to be used throughout the execution
	vars.put("PAYER-ID", vars.get("payerId"))

	//The JWT token and Payer ID is returned as response of this sampler
	SampleResult.setResponseData("JWTToken = " + vars.get("jwtToken") + "\nPayer ID = " + vars.get("payerId"))
}
catch (Exception e)
{
	SampleResult.setSuccessful(false)
	SampleResult.setResponseData("Exception details = " + e)
	SampleResult.setResponseMessage("JWT Token generation not successful. See response data for more details")
}

def count = vars.get("counter")

//Will extract the bill info based on the counter and assigns to temp variables
def billNumber = count
def id = vars.get("bill_id_" + count)
def amount = vars.get("bill_amount_" + count)
def billerName = vars.get("bill_billerName_" + count)
def ifsc = vars.get("bill_ifsc_" + count)
def accountNumber = vars.get("bill_accountNumber_" + count)

//Decrease the counter
count--

//Assigns the bill details to variables to be used for bill payment and for subsequent verifications
vars.put("billNumber", billNumber)
vars.put("counter", count)
vars.put("amount", amount)
vars.put("ifsc", ifsc)
vars.put("accountNumber", accountNumber)

//Bill Information is returned as response of this sampler
SampleResult.setResponseData("--------------------- Information for Bill # " + billNumber + " ---------------------\nID = " + id + "\nAmount = " + amount + "\nBillerName = " + billerName + "\nIFSC = " + ifsc + "\nAccountNumber = " + accountNumber)

//gets the pay and callback response transaction IDs
def pay_transactionId = vars.get("pay_transactionId")
def callback_transactionId = vars.get("callback_transactionId")

//these are used to store the sampler response message
StringBuilder successMessage = new StringBuilder()
StringBuilder errorMessage = new StringBuilder()

//flag to evaluate the comparison
def errorFlag = false

//compare the transaction IDs
if (pay_transactionId.equals(callback_transactionId))
{
	successMessage.append("\nTransaction IDs are matching. \nPay response Transaction ID = " + pay_transactionId + "\nCallback response Transaction ID = " + callback_transactionId)
} else
{
	errorFlag = true
	errorMessage.append("\nTransaction IDs are NOT matching! \nPay response Transaction ID = " + pay_transactionId + "\nCallback response Transaction ID = " + callback_transactionId)
}

//compare the Bill info details with the callback response details

if (errorFlag)
{
	SampleResult.setSuccessful(false)
	SampleResult.setResponseData(errorMessage.toString())
	SampleResult.setResponseMessage("Callback validation failed. See response data for more details")
} else 
{
	SampleResult.setResponseData(successMessage.toString())	
}

