def pay_transactionId = vars.get("pay_transactionId")
def callback_transactionId = vars.get("callback_transactionId")
def error = vars.get("callback_error")
def success = vars.get("callback_success")

StringBuilder successMessage = new StringBuilder()
StringBuilder errorMessage = new StringBuilder()
def errorFlag = false

if (pay_transactionId.equals(callback_transactionId))
{
	successMessage.append("\nTransaction IDs are matching. \nPay response for Transaction ID = " + pay_transactionId + "\nCallback response for Transaction ID = " + callback_transactionId)
} else
{
	errorFlag = true
	errorMessage.append("\nTransaction IDs are NOT matching! \nPay response for Transaction ID = " + pay_transactionId + "\nCallback response for Transaction ID = " + callback_transactionId)
}

if (success.equals("false"))
{
	successMessage.append("\nExpected Callback response for Success value. \nCallback response for Success = " + success)
} else
{
	errorFlag = true
	errorMessage.append("\nUnexpected Callback response for Success value! \nCallback response for Success = " + success)
}

if (error.equals("Amount did not match"))
{
	successMessage.append("\nExpected Callback response for Error value. \nCallback response for Error = " + error)
} else
{
	errorFlag = true
	errorMessage.append("\nUnexpected Callback response for Error value! \nCallback response for Error = " + error)
}


if (errorFlag)
{
	SampleResult.setSuccessful(false)
	SampleResult.setResponseData(errorMessage.toString())
	SampleResult.setResponseMessage("Callback validation failed. See response data for more details")
} else 
{
	SampleResult.setResponseData(successMessage.toString())	
}

